    private void Cuadro_NombreKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_Cuadro_NombreKeyTyped
        // TODO add your handling code here:
        char v;
        
        v=evt.getKeyChar();
        
        if((v<'A' || v>'Z') && (v<' ' || v>' ')){
            
            evt.consume();
        }
        
    }//GEN-LAST:event_Cuadro_NombreKeyTyped

    private void Cuadro_CedulaKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_Cuadro_CedulaKeyTyped
        // TODO add your handling code here:
        
        char v;
        
        v=evt.getKeyChar();
        
        if((v<'0' || v>'9')){
            
            evt.consume();
        }
    }//GEN-LAST:event_Cuadro_CedulaKeyTyped

    private void Cuadro_TelefonoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_Cuadro_TelefonoKeyTyped
        // TODO add your handling code here:
        char v;
        
        v=evt.getKeyChar();
        
        if((v<'1' || v>'9')){
            
            evt.consume();
        }
    }//GEN-LAST:event_Cuadro_TelefonoKeyTyped

